import React from "react";
import Form from "../form/Form";
import { withRouter } from "react-router-dom";
import Button from "../button/Button";

const Inquiry = (props) => {
  var isfilled = true;
  const massiv = []; //onchange event deer input field-d bga utgiig uurtuu hadgalah massiv
  massiv.length = 3; //massiv-n urt
  const changed_fnc = (event) => {
    massiv[event.target.id] = event.target.value; //massiv-n [id] dahi slot-d tuhain id-tai input-n value-g hiij bn
  };
  const clk = () => {
    massiv.forEach((element) => {
      console.log(element);
      if (element === "") {
        isfilled = false;
      }
    });
    if (isfilled) {
      props.history.push({
        pathname: "/api/showdata",
        ugugdul: "inquiry",
      });
    } else {
      window.alert("Please fill all forms !");
    }
  };

  return (
    <div className="pagez">
      <h1>Гүйлгээ</h1>
      <div className="form_holder">
        <Form changed={changed_fnc} placeholder="checksum" id="1" />
        <Form changed={changed_fnc} placeholder="transaction_id" id="2" />
      </div>
      <Button onclk={clk} whichcomp="inquiry"></Button>
    </div>
  );
};

export default withRouter(Inquiry);
