import React, { useEffect, useState } from "react";
import Button from "../button/Button";
function Showdata() {
  useEffect(() => {
    fetchItems();
  }, []);

  const [items, setItems] = useState([]);

  const fetchItems = async () => {
    const data = await fetch("/showdata");
    const items = await data.json();
    setItems(items);
  };
  switch (this.props.whichcomp) {
    case "inquiry":
      console.log("this is inquiry");
      break;
    case "invoice":
      console.log("this is invoice");
      break;
    case "tokens":
      console.log("this is tokens");
      break;
    default:
      console.log("this is home");
      Redirect("http//localhost:3006/");
      break;
  }
  return (
    <div>
      <section>
        {items.map((item) => (
          <div>
            <p>{item.name}</p>
            <p>{item.number}</p>
            <p>{item.height}</p>
            <Button txt="Back"></Button>
          </div>
        ))}
      </section>
    </div>
  );
}

export default Showdata;
