import React, { useEffect, useState } from "react";
import Button from "../button/Button";
import { withRouter } from "react-router-dom";
import axios from "axios";
function Showdata(props) {
  const [items, setItems] = useState([]);
  const [dum, setdum] = useState("");
  useEffect(() => {
    fetchItems();
  }, []);

  useEffect(() => {
    turshilt();
  });

  const turshilt = async () => {
    const data = await fetch("/showdata");
    const items = await data.json();
    setItems(items);
  };

  //componentdidmount
  const fetchItems = async () => {
    const query = new URLSearchParams(props.location.nariin_ugugdul);
    const massiv = {}; //comp's irsen data
    var tooluur = 0;
    for (let param of query.entries()) {
      massiv[tooluur] = param[0];
      tooluur += 1;
    }
    axios.post("http://localhost:3006/showdatas", massiv).then((response) => {
      alert("Таны хүсэлтийг амжилттай хүлээн авлаа");
      setdum("200");
    });
  };
  //ali hesgees submit darsnaaas hamaarulj uurchluh heseg start
  const code_inquiry = (
    <div>
      <section>
        {/* {items.map((item) => (
          <div>
            <h1>Results that came from Гүйлгээ шалгах</h1>
            <p>{item.name}</p>
            <p>{item.number}</p>
            <p>{item.height}</p>
            <Button txt="Back"></Button>
            <Button txt="clkonme"></Button>
          </div>
        ))} */}
      </section>
    </div>
  );
  const code_invoice = (
    <div>
      <section>
        {/* {items.map((item) => (
          <div>
            <h1>Results that came from Нэхэмжлэл үүсгэх</h1>
            <p>{item.name}</p>
            <p>{item.number}</p>
            <Button txt="Back"></Button>
          </div>
        ))} */}
      </section>
    </div>
  );
  const code_tokens = (
    <div>
      <section>
        <div>
          <h1>Results that came from Токеноор гүйлгээ хийх</h1>
          <p>{items["amount"]}</p>
          {/* <p>{item.height}</p>
          <p>{item.number}</p> */}
          <Button txt="Back"></Button>
        </div>
      </section>
    </div>
  );
  //ali hesgees submit darsnaaas hamaarulj uurchluh heseg end
  switch (props.location.ugugdul) {
    case "inquiry":
      return code_inquiry;
    case "invoice":
      return code_invoice;
    case "tokens":
      return code_tokens;
    default:
      props.history.push("/");
      return <div></div>;
  }
}

export default withRouter(Showdata);
