import React from "react";
import Form from "../form/Form";
import { withRouter } from "react-router-dom";
import Button from "../button/Button";
import { Dropdown, Selection } from "react-dropdown";
import "react-dropdown/style.css";
const Invoice = (props) => {
  let isfilled = true;
  const massiv = []; //onchange event deer input field-d bga utgiig uurtuu hadgalah massiv
  massiv.length = 7; //massiv-n urt
  const changed_fnc = (event) => {
    const re = /^[0-9\b]+$/;
    if (event.target.value === "" || re.test(event.target.value)) {
      this.event.value = event.target.value;
      massiv[event.target.id] = event.target.value; //massiv-n [id] dahi slot-d tuhain id-tai input-n value-g hiij bn
    }
  };
  const clk = () => {
    var i = 1;
    for (i; i < massiv.length; i++) {
      //input-vvdig hooson baigaa esehiig shalgah heseg
      if (massiv[i] == null || massiv[i] === "") {
        //hooson baiwal dawtaltig zogsoono
        window.alert("Please fill all the boxes !");
        isfilled = false;
        break;
      }
      isfilled = true;
    }
    if (isfilled) {
      const query = massiv.join("&");
      //hooson bish baiwal showdata component-g haruulna
      props.history.push({
        pathname: "/api/showdata",
        ugugdul: "invoice",
        nariin_ugugdul: query,
      });
    }
  };
  const options = ["one", "two", "three"];
  const defaultOption = options[0];
  return (
    <div className="pagez">
      <h1>Нэхэмжлэл</h1>
      <div className="form_holder">
        <Dropdown
          options={options}
          onChange={this._onSelect}
          value={defaultOption}
          placeholder="Select an option"
        />
        ;
        <Form changed={changed_fnc} placeholder="amount" id="1" />
        <Form changed={changed_fnc} placeholder="callback" id="2" />
        <Form changed={changed_fnc} placeholder="checksum" id="3" />
        <Form changed={changed_fnc} placeholder="genToken" id="4" />
        <Form changed={changed_fnc} placeholder="returnType" id="5" />
        <Form changed={changed_fnc} placeholder="transactionId" id="6" />
      </div>
      <div id="buuz"></div>
      <Button onclk={clk} whichcomp="invoice"></Button>
    </div>
  );
};

export default withRouter(Invoice);
