import React, { useEffect, useState } from "react";
import Button from "../button/Button";
function Showdata(props) {
  useEffect(() => {
    //componentdidmount function
    fetchItems();
  }, []);

  //var whichcomp = "";

  const [items, setItems] = useState([]);
  const [whichcomp1, setcomp] = useState(props.location.ugugdul); //turshilt
  const fetchItems = async () => {
    ///////////didmount ued hiih ymnuud
    // const getdata = new URLSearchParams(props.location.ugugdul);
    // for (let a of getdata.entries()) {
    //   break;
    // }
    //console.log(whichcomp);

    const data = await fetch("/showdata");
    const items = await data.json();
    setItems(items);
  };
  const code_inquiry = (
    <div>
      <section>
        {items.map((item) => (
          <div>
            <h1>This is from inquiry</h1>
            <p>{item.name}</p>
            <p>{item.number}</p>
            <p>{item.height}</p>
            <Button txt="Back"></Button>
          </div>
        ))}
      </section>
    </div>
  );
  const code_invoice = (
    <div>
      <section>
        {items.map((item) => (
          <div>
            <h1>This is from invoice</h1>
            <p>{item.name}</p>
            <p>{item.number}</p>
            <Button txt="Back"></Button>
          </div>
        ))}
      </section>
    </div>
  );
  // const code_tokens = (
  //   <div>
  //     <section>
  //       {items.map((item) => (
  //         <div>
  //           <h1>This is from tokens</h1>
  //           <p>{item.name}</p>
  //           <p>{item.height}</p>
  //           <Button txt="Back"></Button>
  //         </div>
  //       ))}
  //     </section>
  //   </div>
  // );

  switch ("inquiry") {
    case "inquiry":
      return code_inquiry;
      break;
    case "invoice":
      return code_invoice;
      break;
    case "tokens":
      break;
    default:
      return (
        <div>
          <h1>Error</h1>
        </div>
      );

      break;
  }
}

export default Showdata;
