import React, { useEffect, useState } from "react";
import Button from "../button/Button";
import { withRouter } from "react-router-dom";
import axios from "axios";
function Showdata(props) {
  const [items, setItems] = useState([]);
  const [dum, setdum] = useState("");
  useEffect(() => {
    postitems();
  }, []);

  useEffect(() => {
    getdatas();
  });

  const getdatas = async () => {
    const data = await fetch("/showdata");
    const items = await data.json();

    setItems(items);
  };

  //componentdidmount
  const postitems = async () => {
    const query = new URLSearchParams(props.location.nariin_ugugdul);
    const massiv = {}; //comp's irsen data
    var tooluur = 0;
    for (let param of query.entries()) {
      massiv[tooluur] = param[0];
      tooluur += 1;
    }
    axios.post("http://localhost:3006/showdatas", massiv).then((response) => {
      alert("Таны хүсэлтийг амжилттай хүлээн авлаа");
      setdum("200");
    });
  };
  //ali hesgees submit darsnaaas hamaarulj uurchluh heseg start

  //ali hesgees submit darsnaaas hamaarulj uurchluh heseg end

  switch (props.location.ugugdul) {
    case "inquiry":
      if (items.status == "400") {
        const code_inquiry = (
          <div>
            <section>
              <h1>Results that came from Гүйлгээ шалгах</h1>
              <div className="show_results_mid">
                Таны хайсан Transaction ID олдсонгүй
              </div>
              <Button txt="Back"></Button>
            </section>
          </div>
        );
        return code_inquiry;
      } else {
        const code_inquiry = (
          <div>
            <section>
              <h1>Results that came from Гүйлгээ шалгах</h1>
              <div className="show_results">
                <p>
                  <span className="description_holder">
                    Банкны дугаар/bankCode/:
                  </span>
                  {items.bankCode}
                </p>
                <p>
                  <span className="description_holder">Хэмжээ/amount/:</span>
                  {items.amount}
                </p>
                <p>
                  <span className="description_holder">
                    Алдааны дугаар/errorCode/:
                  </span>
                  {items.errorCode}
                </p>
                <p>
                  <span className="description_holder">
                    Алдааны тайлбар/errorDesc/:
                  </span>
                  {items.errorDesc}
                </p>
                <p>
                  <span className="description_holder">Checksum:</span>
                  {items.checksum}
                </p>
                <p>
                  <span className="description_holder">
                    Карт эзэмшигч/cardHolder/:
                  </span>
                  {items.cardHolder}
                </p>
                <p>
                  <span className="description_holder">
                    Гүйлгээний дугаар/TransactionId/:
                  </span>
                  {items.transactionId}
                </p>
                <p>
                  <span className="description_holder">
                    Картын дугаар/Card Number/:
                  </span>
                  {items.cardNumber}
                </p>
                <p>
                  <span className="description_holder">Status:</span>
                  {items.status}
                </p>
                <p>
                  <span className="description_holder">Token:</span>
                  {items.token}
                </p>
              </div>
              <Button txt="Back"></Button>
              <Button txt="See more"></Button>
            </section>
          </div>
        );
        return code_inquiry;
      }

    case "invoice":
      if (items.status == "400") {
        const code_invoice = (
          <div>
            <section>
              <h1>Results that came from Нэхэмжлэл үүсгэх</h1>
              <div className="show_results_mid">Илэрц байхгүй</div>
              <Button txt="Back"></Button>
            </section>
          </div>
        );
        return code_invoice;
      } else {
        const code_invoice = (
          <div>
            <section>
              <h1>Results that came from Нэхэмжлэл үүсгэх</h1>
              <div className="show_results">
                <p>Invoice:{items.invoice}</p>
                <p>Гүйлгээний дугаар/TransactionId/:{items.transactionId}</p>
                <p>Checksum:{items.checksum}</p>
              </div>
              <Button txt="Back"></Button>
            </section>
          </div>
        );
        return code_invoice;
      }

    case "tokens":
      if (items.status == "400") {
        const code_tokens = (
          <div>
            <section>
              <h1>Results that came from Токеноор гүйлгээ хийх</h1>
              <div className="show_results_mid">
                Таны хайсан Token олдсонгүй
              </div>
              <Button txt="Back"></Button>
            </section>
          </div>
        );
        return code_tokens;
      } else {
        const code_tokens = (
          <div>
            <section>
              <h1>Results that came from Токеноор гүйлгээ хийх</h1>
              <div className="show_results">
                <p>
                  Хэмжээ/Amount/:<span>{items.amount}</span>
                </p>
                <p>Тайлбар/errorDesc/:{items.errorDesc}</p>
                <p>Алдааны дугаар/errorCode/:{items.errorCode}</p>
                <p>Checksum:{items.checksum}</p>
                <p>Гүйлгээний дугаар/TransactionId/:{items.transactionId}</p>
                <p>Картын дугаар/Card Number/:{items.cardNumber}</p>
              </div>
              <Button txt="Back"></Button>
            </section>
          </div>
        );
        return code_tokens;
      }
    default:
      props.history.push("/");
      return <div></div>;
  }
}

export default withRouter(Showdata);
